import React, { useRef } from 'react';
import emailjs from '@emailjs/browser';
import FormComponent from './components/FormComponent/FormComponent';
import FieldTypes from './components/FormComponent/FieldTypes';

function ContactUs() {


    const Fields = {
        Field: [
            {
                identifier: 'FullName',
                text: 'First & Last Name:',
                type: FieldTypes.INPUT_TEXT
            },
            {
                identifier: 'Adress',
                text: 'Mailing Address:',
                type: FieldTypes.INPUT_TEXT
            }
        ]
    };

    const formData = useRef();

    function sendEmail(e) {
        e.preventDefault();
        e.target.elements.message.value = "test";
        console.log(e.target.elements.message.value);
        // emailjs.sendForm('service_ey81c06', 'template_1rlbuup', form.current, 'Hudm_HPE7Tvg54jDU')
        //   .then((result) => {
        //       console.log(result.text);
        //   }, (error) => {
        //       console.log(error.text);
        //   });
    };

    function getTypeHTML(formtype, identifier) {
        switch (formtype) {
            case FormTypes.INPUT_TEXT:
                return "<input type='text' name='" + identifier + "'/>";
            default: break;
        }
    }

    return (
        <FormComponent form={formData} onsubmit={sendEmail} fielddata={Fields}/> 
        // <form ref={form} onSubmit={sendEmail}>
        //     {
        //         questionaire.questions.forEach(element => {
        //             getTypeHTML(element.fieldType, element.identifier);
        //         })

        //     }
        //     {/* <label>Name</label>
        //     <input type="text" name="user_name" />
        //     <label>Email</label>
        //     <input type="email" name="user_email" />
        //     <label>Message</label> */}
        //     <textarea name="message" hidden />
        //     <input type="submit" value="Send" />
        // </form>
    );
};

export default ContactUs;
